import React, { useState } from 'react';

function SkillsAndSpells() {
  const [items, setItems] = useState([
    // Add your items here as needed...
    {
      name: 'Item 1',
      lvl: 5,
      damage: 30,
      scaling: 0.5,
      scalingType: 'Physical',
      requirements: 'Strength 20, Dexterity 15',
      effect:
        'Increases attack powerIncreases attack powerIncreases Increases attack powerIncreases attack powerIncreases attack powerIncreases attack powerIncreases attack power attack powerIncreases attack powerIncreases attack powerIncreases attack powerIncreases attack powerIncreases attack powerIncreases attack powerIncreases attack power.',
    },
    // Add more items here as needed...
  ]);

  const [sorting, setSorting] = useState('asc');

  const toggleSorting = () => {
    setSorting(sorting === 'asc' ? 'desc' : 'asc');
    // Sorting logic here based on the "LV" property
    const sortedItems = [...items].sort((a, b) =>
      sorting === 'asc' ? a.lvl - b.lvl : b.lvl - a.lvl
    );
    setItems(sortedItems);
  };

  return (
    <div className="h-auto bg-[#D6E6F6] flex">
      <div className="bg-[#263895] m-auto w-[85%] mt-[2%] rounded-[2%] mb-[5%]   h-[1700px]">
        <div className="bg-[#798EC8] m-[2%] h-[93%] rounded-[2%]   ">
          <div className="flex h-[13%] justify-between">
            <h1 className="text-7xl p-[2%]   ">Weapons</h1>
            <input
              type="search"
              className="rounded-3xl h-[20%] w-[23%] m-[2%] pl-[1%] text-xl bg-[#D6E6F6] text-[black] placeholder:text-[black] font-semibold"
              placeholder="Search .."
            />
          </div>

          <div className="grid grid-cols-8 gap-4 px-4 py-2 bg-[#798EC8]">
            <div className="text-xl font-semibold">Name</div>
            <div
              className="text-xl font-semibold cursor-pointer"
              onClick={toggleSorting}
              title="Click to sort by LV"
            >
              LV {sorting === 'asc' ? '▼' : '▲'}
            </div>
            <div className="text-xl font-semibold">Amount</div>
            <div className="text-xl font-semibold">Type</div>
            <div className="text-xl font-semibold">Scaling</div>
            <div className="text-xl font-semibold">%-Chance</div>
            <div className="text-xl font-semibold">Element</div>
            <div className="text-xl font-semibold">Usages</div>
            <div className="text-xl font-semibold">Explanation</div>
          </div>

          <div className="overflow-scroll h-[75%] overflow-x-hidden scrollbar">
            {items.map((item, index) => (
              <div key={index} className="grid grid-cols-8 gap-4 px-4 py-2">
                <div className="text-xl">{item.name}</div>
                <div className="text-xl">{item.lvl}</div>
                <div className="text-xl">{item.damage}</div>
                <div className="text-xl">{item.scaling}</div>
                <div className="text-xl">{item.scalingType}</div>
                <div className="text-xl">{item.requirements}</div>
                <div className="text-xl">{item.effect}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default SkillsAndSpells;
