import React, { useState } from 'react';
import './App.css';

function App() {
  const [items, setItems] = useState([
    // Add your items here as needed...
    {
      name: 'Sword of Strength',
      lvl: 5,
      damage: 30,
      scaling: 0.5,
      scalingType: 'Physical',
      requirements: 'Strength 20, Dexterity 15',
      effect: 'Increases attack power.',
    },
    {
      name: 'Staff of Wisdom',
      lvl: 8,
      damage: 50,
      scaling: 1.2,
      scalingType: 'Magical',
      requirements: 'Intelligence 25',
      effect: 'Boosts magical abilities.',
    },
    // Add more items here as needed...
  ]);

  const [sorting, setSorting] = useState('asc'); // State to store the sorting order

  const toggleSorting = () => {
    setSorting(sorting === 'asc' ? 'desc' : 'asc');
    // Sorting logic here based on the "LV" property
    const sortedItems = [...items].sort((a, b) =>
      sorting === 'asc' ? a.lvl - b.lvl : b.lvl - a.lvl
    );
    setItems(sortedItems);
  };

  return (
    <div className="h-screen bg-[#D6E6F6] flex items-center justify-center">
      <div className="bg-[#263895] w-[85%] rounded-[2%]">
        <div className="bg-[#798EC8] px-4 py-2 flex justify-between">
          <h1 className="text-7xl">Weapons</h1>
          <input
            type="search"
            className="rounded-3xl h-[30%] w-[23%] pl-[1%] text-xl bg-[#D6E6F6] text-[black] placeholder:text-[black] font-semibold"
            placeholder="Search .."
          />
        </div>

        {/* Custom Scrollable Grid */}
        <div className="overflow-x-hidden overflow-y-scroll max-h-[75%]">
          <div className="grid grid-cols-7 gap-4 px-4 py-2 bg-[#798EC8]">
            {/* Header with the sorting triangle */}
            <div className="text-xl font-semibold">Name</div>
            <div
              className="text-xl font-semibold cursor-pointer"
              onClick={toggleSorting}
              title="Click to sort by LV"
            >
              LV {sorting === 'asc' ? '▼' : '▲'}
            </div>
            <div className="text-xl font-semibold">Damage</div>
            <div className="text-xl font-semibold">Scaling</div>
            <div className="text-xl font-semibold">Scaling Type</div>
            <div className="text-xl font-semibold">Requirements</div>
            <div className="text-xl font-semibold">Effect</div>
          </div>

          {/* Using map to loop through the sorted items array */}
          {items.map((item, index) => (
            <div key={index} className="grid grid-cols-7 gap-4 px-4 py-2">
              <div className="text-xl">{item.name}</div>
              <div className="text-xl">{item.lvl}</div>
              <div className="text-xl">{item.damage}</div>
              <div className="text-xl">{item.scaling}</div>
              <div className="text-xl">{item.scalingType}</div>
              <div className="text-xl">{item.requirements}</div>
              <div className="text-xl">{item.effect}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
